import React from 'react'
import {
	Redirect
} from 'react-router-dom'
import {requestLoginUser} from "../modules/auth";
import {useDispatch} from "react-redux";

const Login = () => {
	const dispatch = useDispatch()
	const { from } = this.props.location.state || { from: { pathname: '/' } }

	if (user !== null) {
		return <Redirect to={from} />
	}


	return (
        <div className="kt-grid__item kt-grid__item--fluid kt-grid kt-grid--ver kt-page">
            <div className="kt-grid__item kt-grid__item--fluid kt-grid kt-grid--hor kt-wrapper" id="kt-wrapper">
                <div className="kt-container kt-grid__item kt-grid__item--fluid kt-grid--hor"
                     style={{
                         maxWidth: "500px",
                     }}
                >
                    <div className="kt-portlet justify-content-lg-center">
                        <div className="kt-portlet__head">
                            <div className="kt-portlet__head-label">
                                <div style={{
                                    fontWeight: "500",
                                    fontSize: "19.5px",
                                }}>
                                    Sign to Account
                                </div>
                            </div>
                        </div>
                        <div className="kt-form" style={{
                            width: "90%",
                            margin: "2rem auto",
                        }}>
                            <div className="form-group">
                                <input className="form-control" type="text" placeholder="Username"
                                       name="username"/>
                            </div>
                            <div className="form-group">
                                <input className="form-control" type="password" placeholder="Password"
                                       name="password"/>
                            </div>
                            <div style={{
                                display: "flex",
                                justifyContent: "space-between"
                            }}>
                                <div className="kt-link kt-link--brand">
                                    Forgot Password ?
                                </div>
                                <div className="btn btn-brand btn-elevate btn-pill"
                                     id="kt_login_submit"
									 onClick={() => dispatch(requestLoginUser())}>Sign In
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default Login
